class FileSystemWatcher {
    static WatchMap := Map()

    __New() {
        this.watches := Map()
    }

    Watch(directory, callback) {
        if !DirExist(directory)
            throw Error("Directory does not exist: " directory)

        watcher := FileSystemWatcher._CreateWatcher(directory, callback)
        this.watches[directory] := watcher
        return this
    }

    static _CreateWatcher(directory, callback) {
        static FILE_NOTIFY_CHANGE_FILE_NAME := 0x1
        static FILE_NOTIFY_CHANGE_LAST_WRITE := 0x10

        flags := FILE_NOTIFY_CHANGE_FILE_NAME | FILE_NOTIFY_CHANGE_LAST_WRITE

        watcher := CreateObject("WScript.Shell").Exec("cmd /c powershell -Command -")
        script := "
        $watcher = New-Object System.IO.FileSystemWatcher
        $watcher.Path = '" directory "'
        $watcher.IncludeSubdirectories = $true
        $watcher.EnableRaisingEvents = $true
        "

        watcher.StdIn.Write(script)
        watcher.StdIn.Close()

        return watcher
    }
}

