class ClipboardEditorGui {
  static Cases := Map(
    "upper", "UPPERCASE",
    "lower", "lowercase",
    "title", "Title Case"
  )

  __New() {
    this.gui := Gui("+AlwaysOnTop", "Clipboard Text Editor")
    this.gui.SetFont("s10", "Segoe UI")
    this.gui.OnEvent("Close", this.CloseGui.Bind(this))
    this.gui.OnEvent("Escape", this.CloseGui.Bind(this))

    this.editCtrl := this.gui.AddEdit("w400 h300 vEditText +Multi +WantReturn +VScroll")

    this.gui.AddButton("w130", ClipboardEditorGui.Cases["upper"]).OnEvent("Click", this.ChangeCase.Bind(this, "upper"))
    this.gui.AddButton("w130 x+5", ClipboardEditorGui.Cases["lower"]).OnEvent("Click", this.ChangeCase.Bind(this, "lower"))
    this.gui.AddButton("w130 x+5", ClipboardEditorGui.Cases["title"]).OnEvent("Click", this.ChangeCase.Bind(this, "title"))

    this.gui.AddButton("w200 yp+40 x10", "Save && Close").OnEvent("Click", this.SaveAndClose.Bind(this))
    this.gui.AddButton("w200 x+5", "Cancel").OnEvent("Click", this.CloseGui.Bind(this))

    this.LoadClipboard()
    this.gui.Show()
  }

  LoadClipboard() {
    local initialText := A_Clipboard
    this.editCtrl.Value := initialText
    this.editCtrl.Focus()
    SendInput("{End}") ; Move caret to end to deselect text
  }

  ChangeCase(caseType, *) {
    local currentText := this.editCtrl.Value
    local newText := ""
    try {
      if (caseType = "upper") {
        newText := StrUpper(currentText)
      } else if (caseType = "lower") {
        newText := StrLower(currentText)
      } else if (caseType = "title") {
        newText := StrTitle(currentText)
      } else {
        return ; Unknown case type
      }
      this.editCtrl.Value := newText
    } catch as e {
      MsgBox("Error changing case: " e.Message)
    }
  }

  SaveAndClose(*) {
    try {
      A_Clipboard := this.editCtrl.Value
      this.gui.Destroy()
    } catch as e {
      MsgBox("Error saving to clipboard: " e.Message)
    }
  }

  CloseGui(*) {
    this.gui.Destroy()
  }
}

