class ClipboardEditor {
    __New() {
        this.controls := Map()
        this.gui := Gui("+Resize", "Clipboard Editor")
        this.gui.SetFont("s10")

        this.gui.OnEvent("Close", (*) => this.gui.Hide())
        this.gui.OnEvent("Escape", (*) => this.gui.Hide())

        this.controls["clipEdit"] := this.gui.AddEdit("w400 h200 vClipText")

        this.gui.AddText("xm w400 h1 0x10")  ; Separator line
        this.controls["upperBtn"] := this.gui.AddButton("x10 y+10 w120", "UPPERCASE").OnEvent("Click", this.UpperCase.Bind(this))
        this.controls["lowerBtn"] := this.gui.AddButton("x+10 w120", "lowercase").OnEvent("Click", this.LowerCase.Bind(this))
        this.controls["titleBtn"] := this.gui.AddButton("x+10 w120", "Title Case").OnEvent("Click", this.TitleCase.Bind(this))
        this.controls["saveBtn"] := this.gui.AddButton("xm y+10 w400", "Save to Clipboard").OnEvent("Click", this.UpdateClipboard.Bind(this))

        this.ShowClipboard()
        this.gui.Show()
    }

    ShowClipboard() {
        try {
            if (A_Clipboard != "") {
                this.controls["clipEdit"].Value := A_Clipboard
            }
        } catch as err {
            this.controls["clipEdit"].Value := "Error reading clipboard: " err.Message
        }
    }

    LowerCase(*) {
        currentText := this.controls["clipEdit"].Value
        this.controls["clipEdit"].Value := Format("{:L}", currentText)
    }

    UpperCase(*) {
        currentText := this.controls["clipEdit"].Value
        this.controls["clipEdit"].Value := Format("{:U}", currentText)
    }

    TitleCase(*) {
        currentText := this.controls["clipEdit"].Value
        this.controls["clipEdit"].Value := Format("{:T}", currentText)
    }

    UpdateClipboard(*) {
        try {
            A_Clipboard := this.controls["clipEdit"].Value
            ToolTip("Clipboard updated!")
            SetTimer () => ToolTip(), -1000
        } catch as err {
            ToolTip("Error updating clipboard: " err.Message)
            SetTimer () => ToolTip(), -2000
        }
    }
}

