UpdateCpuUsage() {
    usage := GetCPUUsage()
    ToolTip("CPU Usage: " usage "%")
}

GetCPUUsage() {
    static PdhOpenQuery := DllCall("LoadLibrary", "Str", "pdh", "Ptr")
    static counterPath := "\\\\Processor(_Total)\\\\% Processor Time"
    static query := 0, counter := 0

    if (!query) {
        DllCall("pdh\PdhOpenQuery", "Ptr", 0, "Ptr", 0, "Ptr*", &query)
        DllCall("pdh\PdhAddCounter", "Ptr", query, "Str", counterPath, "Ptr", 0, "Ptr*", &counter)
        DllCall("pdh\PdhCollectQueryData", "Ptr", query)
        Sleep 250
    }

    DllCall("pdh\PdhCollectQueryData", "Ptr", query)

    value := 0, formatValue := Buffer(16, 0)
    DllCall("pdh\PdhGetFormattedCounterValue", "Ptr", counter, "UInt", 0x00000100, "Ptr", 0, "Ptr", formatValue)
    value := NumGet(formatValue, 8, "Double")

    return Round(value, 1)
}

