class ClipboardEditor {
  __New() {
    this.gui := Gui("+Resize", "Clipboard Text Editor")
    this.gui.SetFont("s10")
    this.gui.OnEvent("Close", (*) => this.gui.Hide())
    this.gui.OnEvent("Escape", (*) => this.gui.Hide())
    this.SetupControls()
    this.LoadClipboardContent()
    this.Show()
  }
  SetupControls() {
    this.editControl := this.gui.AddEdit("w400 h200 vClipboardText")
    this.gui.AddButton("Default w100", "To Lower").OnEvent("Click", this.ToLower.Bind(this))
    this.gui.AddButton("Default w100", "To Upper").OnEvent("Click", this.ToUpper.Bind(this))
    this.gui.AddButton("Default w100", "To Title").OnEvent("Click", this.ToTitle.Bind(this))
    this.gui.AddButton("Default w100", "Save").OnEvent("Click", this.SaveToClipboard.Bind(this))
    this.gui.AddButton("Default w100", "Exit").OnEvent("Click", (*) => this.gui.Hide())
  }
  LoadClipboardContent() {
    this.clipboardText := A_Clipboard
    this.editControl.Text := this.clipboardText
  }
  ToLower(*) {
    this.clipboardText := StrLower(this.editControl.Text)
    this.editControl.Text := this.clipboardText
  }
  ToUpper(*) {
    this.clipboardText := StrUpper(this.editControl.Text)
    this.editControl.Text := this.clipboardText
  }
  ToTitle(*) {
    this.clipboardText := StrReplace(this.editControl.Text, " ", " ")
    this.clipboardText := StrLower(this.clipboardText)
    this.clipboardText := StrUpper(SubStr(this.clipboardText, 1, 1)) . SubStr(this.clipboardText, 2)
    this.editControl.Text := this.clipboardText
  }
  SaveToClipboard(*) {
    A_Clipboard := this.editControl.Text
  }
  Show(*) => this.gui.Show()
  Toggle(*) {
    if WinExist("ahk_id " this.gui.Hwnd)
      this.gui.Hide()
    else
      this.gui.Show()
  }
  __Delete() {
    if WinExist("ahk_id " this.gui.Hwnd)
      this.gui.Close()
  }
}

