class MyEditor {
    __New() {
        this.gui := Gui("+Resize", "Clipboard Editor")
        this.editControl := this.gui.AddEdit("w500 h300 vEditText")
        this.btnLower := this.gui.AddButton("wp", "Lowercase")
        this.btnUpper := this.gui.AddButton("wp", "Uppercase")
        this.btnTitle := this.gui.AddButton("wp", "Title Case")
        this.btnSave := this.gui.AddButton("Default", "OK")

        this.btnLower.OnEvent("Click", this.LowercaseButton_Click.Bind(this))
        this.btnUpper.OnEvent("Click", this.UppercaseButton_Click.Bind(this))
        this.btnTitle.OnEvent("Click", this.TitleCaseButton_Click.Bind(this))
        this.btnSave.OnEvent("Click", this.Submit.Bind(this))
        this.gui.OnEvent("Close", (*) => this.OnGuiClose())

        this.editControl.Value := Clipboard
        this.btnLower.Focus()
        this.gui.Show()
    }

    LowercaseButton_Click(*) {
        text := this.editControl.Value
        this.editControl.Value := StrLower(text)
    }

    UppercaseButton_Click(*) {
        text := this.editControl.Value
        this.editControl.Value := StrUpper(text)
    }

    TitleCaseButton_Click(*) {
        text := this.editControl.Value
        newText := ""
        words := StrSplit(text, " ", "`n", "`t")
        for index, word in words {
            if (word != "") {
                newWord := StrUpper(SubStr(word, 1, 1)) . SubStr(word, 2)
                newText .= newWord
                if (index < words.Length()) 
                    newText .= " "
            }
        }
        this.editControl.Value := newText
    }

    Submit(*) {
        this.SaveToClipboard()
        this.gui.Hide()
    }

    OnGuiClose(*) {
        this.SaveToClipboard()
        this.gui.Hide()
    }

    SaveToClipboard() {
        Clipboard := this.editControl.Value
    }
}
